pair[] C;

A = (1,0);
B = (-1,0);
C[1] = dir(1*180/7);
C[2] = dir(2*180/7);
C[3] = dir(3*180/7);
C[4] = dir(4*180/7);
C[5] = dir(5*180/7);
C[6] = dir(6*180/7);

draw(A--B);
draw(arc((0,0),1,0,180));

for (i = 1; i <= 6; ++i) {
  draw(A--C[i]--B);
  dot("$C_" + string(i) + "$", C[i], C[i]);
}

dot("$A$", A, E);
dot("$B$", B, W);
